import React, { useEffect, useRef, useState } from 'react';
import { Loader2, Video, VideoOff, AlertCircle } from 'lucide-react';

/**
 * MeetingRoom ΓÇö embeds Zoom using the Web SDK CDN approach (ZoomMtg standalone).
 * This works without npm install by loading Zoom's hosted SDK.
 *
 * Props:
 *   meetingNumber  string ΓÇö Zoom meeting ID
 *   signature      string ΓÇö server-generated SDK signature
 *   sdkKey         string ΓÇö Zoom SDK Key (client ID from Meeting SDK app)
 *   password       string ΓÇö meeting password
 *   userName       string ΓÇö display name in meeting
 *   userEmail      string ΓÇö user's email
 *   role           number ΓÇö 0=attendee, 1=host
 *   onLeave        function ΓÇö called when user leaves
 */
export default function MeetingRoom({ meetingNumber, signature, sdkKey, password, userName, userEmail, role = 0, onLeave }) {
    const containerRef = useRef(null);
    const [status, setStatus] = useState('loading'); // loading | joining | joined | error
    const [error, setError] = useState('');
    const sdkLoadedRef = useRef(false);

    useEffect(() => {
        if (!meetingNumber || !signature || !sdkKey) return;
        if (sdkLoadedRef.current) return;
        sdkLoadedRef.current = true;

        loadAndJoin();

        return () => {
            // Cleanup: leave meeting if component unmounts
            try {
                if (window.ZoomMtg) {
                    window.ZoomMtg.leaveMeeting({});
                }
            } catch { /* ignore */ }
        };
    }, [meetingNumber, signature, sdkKey]);

    async function loadAndJoin() {
        try {
            // Inject Zoom SDK stylesheet
            if (!document.getElementById('zoom-sdk-css')) {
                const link = document.createElement('link');
                link.id = 'zoom-sdk-css';
                link.rel = 'stylesheet';
                link.href = 'https://source.zoom.us/3.9.8/css/bootstrap.css';
                document.head.appendChild(link);

                const link2 = document.createElement('link');
                link2.id = 'zoom-sdk-css2';
                link2.rel = 'stylesheet';
                link2.href = 'https://source.zoom.us/3.9.8/css/react-select.css';
                document.head.appendChild(link2);
            }

            // Inject Zoom SDK JS
            if (!window.ZoomMtg) {
                await new Promise((resolve, reject) => {
                    const script = document.createElement('script');
                    script.src = 'https://source.zoom.us/3.9.8/lib/vendor/react.min.js';
                    script.onload = resolve;
                    script.onerror = reject;
                    document.body.appendChild(script);
                });

                await new Promise((resolve, reject) => {
                    const script = document.createElement('script');
                    script.src = 'https://source.zoom.us/3.9.8/lib/vendor/react-dom.min.js';
                    script.onload = resolve;
                    script.onerror = reject;
                    document.body.appendChild(script);
                });

                await new Promise((resolve, reject) => {
                    const script = document.createElement('script');
                    script.src = 'https://source.zoom.us/3.9.8/lib/vendor/redux.min.js';
                    script.onload = resolve;
                    script.onerror = reject;
                    document.body.appendChild(script);
                });

                await new Promise((resolve, reject) => {
                    const script = document.createElement('script');
                    script.src = 'https://source.zoom.us/3.9.8/lib/vendor/redux-thunk.min.js';
                    script.onload = resolve;
                    script.onerror = reject;
                    document.body.appendChild(script);
                });

                await new Promise((resolve, reject) => {
                    const script = document.createElement('script');
                    script.src = 'https://source.zoom.us/3.9.8/zoom-meeting-embedded-3.9.8.min.js';
                    script.onload = resolve;
                    script.onerror = reject;
                    document.body.appendChild(script);
                });
            }

            setStatus('joining');

            const ZoomMtgEmbedded = window.ZoomMtgEmbedded;
            const client = ZoomMtgEmbedded.createClient();

            const meetingSDKElement = containerRef.current;
            if (!meetingSDKElement) return;

            client.init({
                debug: false,
                zoomAppRoot: meetingSDKElement,
                language: 'en-US',
                customize: {
                    meetingInfo: ['topic', 'host', 'mn', 'pwd', 'telPwd', 'invite', 'participant', 'dc', 'enctype'],
                    toolbar: {
                        buttons: [
                            { text: 'Leave', className: 'LeaveButton', onClick: () => { client.leaveMeeting(); onLeave?.(); } },
                        ],
                    },
                },
            });

            await client.join({
                signature,
                sdkKey,
                meetingNumber: String(meetingNumber),
                password: password || '',
                userName,
                userEmail,
                role,
            });

            setStatus('joined');
        } catch (err) {
            console.error('Zoom join error:', err);
            setError(err?.reason || err?.message || 'Failed to join meeting');
            setStatus('error');
        }
    }

    if (status === 'error') {
        return (
            <div className="flex flex-col items-center justify-center h-full text-center p-8">
                <div className="w-16 h-16 rounded-2xl bg-red-100 dark:bg-red-900/20 flex items-center justify-center mb-4">
                    <AlertCircle className="w-8 h-8 text-red-500" />
                </div>
                <h3 className="text-lg font-bold text-slate-800 dark:text-slate-200 mb-2">Failed to Join Meeting</h3>
                <p className="text-sm text-slate-500 dark:text-slate-400 max-w-sm">{error}</p>
                <p className="text-xs text-slate-400 dark:text-slate-600 mt-4">
                    Ensure your Zoom SDK credentials are configured in the server .env file and that HTTPS is enabled.
                </p>
            </div>
        );
    }

    return (
        <div className="relative w-full h-full bg-slate-900 rounded-2xl overflow-hidden">
            {(status === 'loading' || status === 'joining') && (
                <div className="absolute inset-0 flex flex-col items-center justify-center bg-slate-900 z-10">
                    <div className="w-16 h-16 rounded-2xl bg-primary-theme/20 flex items-center justify-center mb-4 animate-pulse">
                        <Video className="w-8 h-8 text-primary-theme" />
                    </div>
                    <div className="flex items-center gap-2 text-slate-400">
                        <Loader2 className="w-4 h-4 animate-spin" />
                        <span className="text-sm">{status === 'loading' ? 'Loading Zoom SDK...' : 'Joining meeting...'}</span>
                    </div>
                </div>
            )}
            {/* Zoom SDK renders into this div */}
            <div ref={containerRef} id="meetingSDKElement" className="w-full h-full" />
        </div>
    );
}

import { useEffect, useState } from 'react';
import api from '@/lib/api';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Search, Check, X, Star, Plus } from 'lucide-react';
import StatusBadge from '@/components/admin/StatusBadge';
import ActionMenu from '@/components/admin/ActionMenu';
import Modal from '@/components/ui/Modal';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';

const TestimonialsManager = () => {
    const [testimonials, setTestimonials] = useState([]);
    const [filteredTestimonials, setFilteredTestimonials] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [statusFilter, setStatusFilter] = useState('all');
    const [isAddModalOpen, setIsAddModalOpen] = useState(false);
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
    const [selectedTestimonial, setSelectedTestimonial] = useState(null);
    const [formData, setFormData] = useState({ name: '', rating: 5, content: '', email: '' });
    const [submitting, setSubmitting] = useState(false);

    useEffect(() => {
        fetchTestimonials();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    useEffect(() => {
        filterTestimonials();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [searchTerm, statusFilter, testimonials]);

    const fetchTestimonials = async () => {
        try {
            const { data } = await api.get('/testimonials/admin');
            setTestimonials(data);
            setFilteredTestimonials(data);
        } catch (err) {
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const filterTestimonials = () => {
        let filtered = testimonials;

        if (searchTerm) {
            filtered = filtered.filter(t =>
                t.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                t.content.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        if (statusFilter !== 'all') {
            filtered = filtered.filter(t => t.status === statusFilter);
        }

        setFilteredTestimonials(filtered);
    };

    const updateStatus = async (id, status) => {
        try {
            await api.put(`/testimonials/${id}`, { status });
            setTestimonials(testimonials.map(t =>
                t.id === id ? { ...t, status } : t
            ));
        } catch (err) {
            console.error('Failed to update status:', err);
        }
    };

    const handleAction = (testimonial, actionType) => {
        setSelectedTestimonial(testimonial);
        if (actionType === 'approve') {
            updateStatus(testimonial.id, 'Approved');
        } else if (actionType === 'reject') {
            updateStatus(testimonial.id, 'Rejected');
        } else if (actionType === 'edit') {
            setIsEditModalOpen(true);
        } else if (actionType === 'delete') {
            setIsDeleteModalOpen(true);
        }
    };

    const handleEditTestimonial = async (e) => {
        e.preventDefault();
        setSubmitting(true);
        try {
            const { data } = await api.put(`/testimonials/${selectedTestimonial.id}`, selectedTestimonial);
            setTestimonials(testimonials.map(t => t.id === selectedTestimonial.id ? data.data : t));
            setIsEditModalOpen(false);
            alert('Testimonial updated successfully');
        } catch (err) {
            console.error('Failed to update testimonial:', err);
            alert(err.response?.data?.error || 'Failed to update testimonial');
        } finally {
            setSubmitting(false);
        }
    };

    const handleDeleteTestimonial = async () => {
        setSubmitting(true);
        try {
            await api.delete(`/testimonials/${selectedTestimonial.id}`);
            setTestimonials(testimonials.filter(t => t.id !== selectedTestimonial.id));
            setIsDeleteModalOpen(false);
            alert('Testimonial deleted successfully');
        } catch (err) {
            console.error('Failed to delete testimonial:', err);
            alert('Failed to delete testimonial');
        } finally {
            setSubmitting(false);
        }
    };

    const handleAddTestimonial = async (e) => {
        e.preventDefault();
        setSubmitting(true);
        try {
            const { data } = await api.post('/testimonials/admin', formData);
            setTestimonials([data.data, ...testimonials]);
            setIsAddModalOpen(false);
            setFormData({ name: '', rating: 5, content: '', email: '' });
            alert('Testimonial added successfully');
        } catch (err) {
            console.error('Failed to add testimonial:', err);
            alert(err.response?.data?.error || 'Failed to add testimonial');
        } finally {
            setSubmitting(false);
        }
    };

    const renderStars = (rating) => {
        return (
            <div className="flex gap-0.5">
                {[...Array(5)].map((_, i) => (
                    <Star
                        key={i}
                        className={`w-4 h-4 ${i < rating ? 'fill-yellow-400 text-yellow-400' : 'text-slate-300'}`}
                    />
                ))}
            </div>
        );
    };

    return (
        <div className="p-3 md:p-6 space-y-4 md:space-y-6">
            {/* Header */}
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
                <div>
                    <h1 className="text-2xl md:text-3xl font-bold text-slate-900 dark:text-white">Testimonials Management</h1>
                    <p className="text-slate-600 dark:text-slate-400 mt-1">Review and manage customer testimonials</p>
                </div>
                <Button onClick={() => setIsAddModalOpen(true)} className="gap-2">
                    <Plus className="w-4 h-4" />
                    Add Testimonial
                </Button>
            </div>

            {/* Filters */}
            <Card>
                <CardContent className="p-4">
                    <div className="flex flex-col md:flex-row gap-4">
                        {/* Search */}
                        <div className="flex-1 relative">
                            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400 w-4 h-4" />
                            <Input
                                placeholder="Search testimonials..."
                                value={searchTerm}
                                onChange={(e) => setSearchTerm(e.target.value)}
                                className="pl-10"
                            />
                        </div>

                        {/* Status Filter */}
                        <div className="flex gap-2 flex-wrap">
                            <Button
                                variant={statusFilter === 'all' ? 'default' : 'outline'}
                                size="sm"
                                onClick={() => setStatusFilter('all')}
                            >
                                All ({testimonials.length})
                            </Button>
                            <Button
                                variant={statusFilter === 'Pending' ? 'default' : 'outline'}
                                size="sm"
                                onClick={() => setStatusFilter('Pending')}
                            >
                                Pending ({testimonials.filter(t => t.status === 'Pending').length})
                            </Button>
                            <Button
                                variant={statusFilter === 'Approved' ? 'default' : 'outline'}
                                size="sm"
                                onClick={() => setStatusFilter('Approved')}
                            >
                                Approved ({testimonials.filter(t => t.status === 'Approved').length})
                            </Button>
                            <Button
                                variant={statusFilter === 'Rejected' ? 'default' : 'outline'}
                                size="sm"
                                onClick={() => setStatusFilter('Rejected')}
                            >
                                Rejected ({testimonials.filter(t => t.status === 'Rejected').length})
                            </Button>
                        </div>
                    </div>
                </CardContent>
            </Card>

            {/* Testimonials Grid */}
            {loading ? (
                <p className="text-center py-8 text-slate-500">Loading testimonials...</p>
            ) : filteredTestimonials.length === 0 ? (
                <p className="text-center py-8 text-slate-500">No testimonials found</p>
            ) : (
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                    {filteredTestimonials.map((testimonial) => (
                        <Card key={testimonial.id} className="hover:shadow-lg transition-shadow">
                            <CardHeader>
                                <div className="flex items-start justify-between">
                                    <div className="flex-1">
                                        <CardTitle className="text-lg">{testimonial.name}</CardTitle>
                                        <div className="mt-2">
                                            {renderStars(testimonial.rating)}
                                        </div>
                                    </div>
                                    <div className="flex items-center gap-2">
                                        <StatusBadge status={testimonial.status} pulse={testimonial.status === 'Pending'} />
                                        <ActionMenu
                                            actions={[
                                                { type: 'edit', label: 'Edit' },
                                                { type: 'approve', label: 'Approve' },
                                                { type: 'reject', label: 'Reject', danger: true },
                                                { type: 'delete', label: 'Delete', danger: true }
                                            ]}
                                            onAction={(action) => handleAction(testimonial, action)}
                                        />
                                    </div>
                                </div>
                            </CardHeader>
                            <CardContent>
                                <p className="text-slate-700 dark:text-slate-300 text-sm leading-relaxed mb-3">
                                    "{testimonial.content}"
                                </p>
                                <div className="flex items-center justify-between text-xs text-slate-500">
                                    <span>Submitted: {new Date(testimonial.created_at).toLocaleDateString()}</span>
                                    {testimonial.status === 'Pending' && (
                                        <div className="flex gap-2">
                                            <Button
                                                size="sm"
                                                variant="outline"
                                                className="h-7 text-xs gap-1 text-green-600 hover:bg-green-50 hover:text-green-700"
                                                onClick={() => updateStatus(testimonial.id, 'Approved')}
                                            >
                                                <Check className="w-3 h-3" />
                                                Approve
                                            </Button>
                                            <Button
                                                size="sm"
                                                variant="outline"
                                                className="h-7 text-xs gap-1 text-red-600 hover:bg-red-50 hover:text-red-700"
                                                onClick={() => updateStatus(testimonial.id, 'Rejected')}
                                            >
                                                <X className="w-3 h-3" />
                                                Reject
                                            </Button>
                                        </div>
                                    )}
                                </div>
                            </CardContent>
                        </Card>
                    ))}
                </div>
            )}

            {/* Add Testimonial Modal */}
            <Modal
                isOpen={isAddModalOpen}
                onClose={() => setIsAddModalOpen(false)}
                title="Add New Testimonial"
            >
                <form onSubmit={handleAddTestimonial} className="space-y-4">
                    <div className="space-y-2">
                        <Label htmlFor="add-name">Customer Name</Label>
                        <Input
                            id="add-name"
                            value={formData.name}
                            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                            placeholder="e.g. Rahul Sharma"
                            required
                        />
                    </div>

                    <div className="space-y-2">
                        <Label htmlFor="add-email">Email (Optional)</Label>
                        <Input
                            id="add-email"
                            type="email"
                            value={formData.email}
                            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                            placeholder="e.g. rahul@example.com"
                        />
                    </div>

                    <div className="space-y-2">
                        <Label htmlFor="add-rating">Rating (1-5)</Label>
                        <div className="flex items-center gap-4">
                            <Input
                                id="add-rating"
                                type="number"
                                min="1"
                                max="5"
                                value={formData.rating}
                                onChange={(e) => setFormData({ ...formData, rating: parseInt(e.target.value) })}
                                className="w-24"
                                required
                            />
                            <div className="flex gap-1">
                                {[...Array(5)].map((_, i) => (
                                    <Star
                                        key={i}
                                        className={`w-5 h-5 cursor-pointer transition-colors ${i < formData.rating ? 'fill-yellow-400 text-yellow-400' : 'text-slate-200'}`}
                                        onClick={() => setFormData({ ...formData, rating: i + 1 })}
                                    />
                                ))}
                            </div>
                        </div>
                    </div>

                    <div className="space-y-2">
                        <Label htmlFor="add-content">Testimonial Content</Label>
                        <Textarea
                            id="add-content"
                            value={formData.content}
                            onChange={(e) => setFormData({ ...formData, content: e.target.value })}
                            placeholder="Write the testimonial here..."
                            rows={4}
                            required
                        />
                    </div>

                    <div className="flex justify-end gap-3 pt-4">
                        <Button type="button" variant="outline" onClick={() => setIsAddModalOpen(false)}>
                            Cancel
                        </Button>
                        <Button type="submit" disabled={submitting}>
                            {submitting ? 'Adding...' : 'Add Testimonial'}
                        </Button>
                    </div>
                </form>
            </Modal>

            {/* Edit Testimonial Modal */}
            <Modal
                isOpen={isEditModalOpen}
                onClose={() => setIsEditModalOpen(false)}
                title="Edit Testimonial"
            >
                {selectedTestimonial && (
                    <form onSubmit={handleEditTestimonial} className="space-y-4">
                        <div className="space-y-2">
                            <Label htmlFor="edit-name">Customer Name</Label>
                            <Input
                                id="edit-name"
                                value={selectedTestimonial.name}
                                onChange={(e) => setSelectedTestimonial({ ...selectedTestimonial, name: e.target.value })}
                                required
                            />
                        </div>

                        <div className="space-y-2">
                            <Label htmlFor="edit-email">Email (Optional)</Label>
                            <Input
                                id="edit-email"
                                type="email"
                                value={selectedTestimonial.email || ''}
                                onChange={(e) => setSelectedTestimonial({ ...selectedTestimonial, email: e.target.value })}
                            />
                        </div>

                        <div className="space-y-2">
                            <Label htmlFor="edit-rating">Rating (1-5)</Label>
                            <div className="flex items-center gap-4">
                                <Input
                                    id="edit-rating"
                                    type="number"
                                    min="1"
                                    max="5"
                                    value={selectedTestimonial.rating}
                                    onChange={(e) => setSelectedTestimonial({ ...selectedTestimonial, rating: parseInt(e.target.value) })}
                                    className="w-24"
                                    required
                                />
                                <div className="flex gap-1">
                                    {[...Array(5)].map((_, i) => (
                                        <Star
                                            key={i}
                                            className={`w-5 h-5 cursor-pointer transition-colors ${i < selectedTestimonial.rating ? 'fill-yellow-400 text-yellow-400' : 'text-slate-200'}`}
                                            onClick={() => setSelectedTestimonial({ ...selectedTestimonial, rating: i + 1 })}
                                        />
                                    ))}
                                </div>
                            </div>
                        </div>

                        <div className="space-y-2">
                            <Label htmlFor="edit-status">Status</Label>
                            <select
                                id="edit-status"
                                value={selectedTestimonial.status}
                                onChange={(e) => setSelectedTestimonial({ ...selectedTestimonial, status: e.target.value })}
                                className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2"
                            >
                                <option value="Pending">Pending</option>
                                <option value="Approved">Approved</option>
                                <option value="Rejected">Rejected</option>
                            </select>
                        </div>

                        <div className="space-y-2">
                            <Label htmlFor="edit-content">Testimonial Content</Label>
                            <Textarea
                                id="edit-content"
                                value={selectedTestimonial.content}
                                onChange={(e) => setSelectedTestimonial({ ...selectedTestimonial, content: e.target.value })}
                                rows={4}
                                required
                            />
                        </div>

                        <div className="flex justify-end gap-3 pt-4">
                            <Button type="button" variant="outline" onClick={() => setIsEditModalOpen(false)}>
                                Cancel
                            </Button>
                            <Button type="submit" disabled={submitting}>
                                {submitting ? 'Saving...' : 'Save Changes'}
                            </Button>
                        </div>
                    </form>
                )}
            </Modal>

            {/* Delete Testimonial Modal */}
            <Modal
                isOpen={isDeleteModalOpen}
                onClose={() => setIsDeleteModalOpen(false)}
                title="Confirm Deletion"
            >
                <div className="space-y-4">
                    <p className="text-slate-600 dark:text-slate-400">
                        Are you sure you want to delete the testimonial from <span className="font-bold text-slate-900 dark:text-white">{selectedTestimonial?.name}</span>? This action cannot be undone.
                    </p>
                    <div className="flex justify-end gap-3 pt-4">
                        <Button variant="outline" onClick={() => setIsDeleteModalOpen(false)}>
                            Cancel
                        </Button>
                        <Button variant="destructive" onClick={handleDeleteTestimonial} disabled={submitting}>
                            {submitting ? 'Deleting...' : 'Delete Testimonial'}
                        </Button>
                    </div>
                </div>
            </Modal>
        </div>
    );
};

export default TestimonialsManager;

import { useState } from 'react';
import { motion } from 'framer-motion';
import { useTheme } from '@/context/ThemeContext';
import api from '@/lib/api';
import { Button } from '@/components/ui/button';
import { Check, Palette, ShieldCheck, Zap, Heart, Moon, Sun } from 'lucide-react';

const ThemeSettings = () => {
    const { themeId, updateGlobalTheme, themes, isDark, toggleGlobalDarkMode } = useTheme();
    const [saving, setSaving] = useState(false);

    const handleThemeChange = async (newThemeId) => {
        setSaving(true);
        try {
            await updateGlobalTheme(newThemeId);
        } catch (err) {
            console.error('Failed to save theme setting:', err);
            alert('Could not save global theme.');
        } finally {
            setSaving(false);
        }
    };

    const handleModeToggle = async () => {
        setSaving(true);
        try {
            await toggleGlobalDarkMode();
        } catch (err) {
            console.error('Failed to save global mode setting:', err);
            alert('Could not save global display mode.');
        } finally {
            setSaving(false);
        }
    };

    return (
        <div className="max-w-4xl mx-auto space-y-8">
            <header>
                <h1 className="text-3xl font-bold text-slate-900 dark:text-white">Appearance Settings</h1>
                <p className="text-slate-500 dark:text-slate-400 mt-2">
                    Customize the look and feel of your consultancy website for all users.
                </p>
            </header>

            {/* Dark Mode Toggle Section */}
            <section className="bg-white dark:bg-slate-900 rounded-3xl p-8 border border-slate-100 dark:border-white/5 shadow-sm">
                <div className="flex items-center justify-between">
                    <div className="space-y-1">
                        <h2 className="text-xl font-bold flex items-center gap-2 text-slate-900 dark:text-white">
                            {isDark ? <Moon className="text-indigo-500" /> : <Sun className="text-amber-500" />}
                            Personal Display Mode
                        </h2>
                        <p className="text-sm text-slate-500">This setting only affects your current browser session.</p>
                    </div>
                    <Button
                        onClick={handleModeToggle}
                        variant="outline"
                        disabled={saving}
                        className="h-12 rounded-xl px-6 gap-2 font-bold"
                    >
                        Switch to {isDark ? 'Light' : 'Dark'} Mode
                    </Button>
                </div>
            </section>

            {/* Theme Presets Section */}
            <section className="space-y-6">
                <div className="flex items-center gap-3">
                    <Palette className="text-primary" />
                    <h2 className="text-2xl font-bold text-slate-900 dark:text-white">Global Color Palette</h2>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {themes.map((t) => {
                        const isActive = themeId === t.id;
                        return (
                            <motion.button
                                key={t.id}
                                whileHover={{ y: -4 }}
                                whileTap={{ scale: 0.98 }}
                                onClick={() => handleThemeChange(t.id)}
                                disabled={saving}
                                className={`relative p-8 rounded-[2.5rem] border-2 text-left transition-all duration-300 ${isActive
                                    ? 'bg-white dark:bg-slate-800 border-primary shadow-xl shadow-primary/10'
                                    : 'bg-white/50 dark:bg-slate-900/50 border-slate-100 dark:border-white/5 hover:border-slate-200'
                                    }`}
                            >
                                {isActive && (
                                    <div className="absolute top-6 right-6 w-8 h-8 bg-primary rounded-full flex items-center justify-center text-white shadow-lg shadow-primary/40">
                                        <Check size={16} strokeWidth={4} />
                                    </div>
                                )}

                                <div className="space-y-6">
                                    <div className="space-y-2">
                                        <h3 className="text-xl font-bold text-slate-900 dark:text-white capitalize">{t.name}</h3>
                                        <div className="flex gap-2">
                                            <div className="w-8 h-8 rounded-full border-2 border-white shadow-sm" style={{ backgroundColor: t.primary }} title="Primary" />
                                            <div className="w-8 h-8 rounded-full border-2 border-white shadow-sm" style={{ backgroundColor: t.secondary }} title="Secondary" />
                                            <div className="w-8 h-8 rounded-full border-2 border-white shadow-sm" style={{ backgroundColor: t.accent }} title="Accent" />
                                        </div>
                                    </div>

                                    {/* Mini Preview Mockup */}
                                    <div className="p-4 rounded-2xl bg-slate-50 dark:bg-slate-950/50 space-y-3">
                                        <div className="flex items-center gap-2">
                                            <div className="w-3 h-3 rounded-full" style={{ backgroundColor: t.primary }} />
                                            <div className="h-2 w-2/3 bg-slate-200 dark:bg-slate-800 rounded-full" />
                                        </div>
                                        <div className="h-8 w-full rounded-xl flex items-center justify-center text-[10px] font-black uppercase text-white shadow-sm" style={{ backgroundColor: t.primary }}>
                                            Primary CTA
                                        </div>
                                        <div className="flex gap-2">
                                            <div className="h-6 flex-1 rounded-lg border flex items-center justify-center text-[8px] font-bold" style={{ borderColor: t.secondary, color: t.secondary }}>Secondary</div>
                                            <div className="h-6 flex-1 rounded-lg flex items-center justify-center text-[8px] font-black text-white" style={{ backgroundColor: t.accent }}>Accent</div>
                                        </div>
                                    </div>

                                    {isActive && (
                                        <div className="flex items-center gap-2 text-primary font-bold text-sm">
                                            <ShieldCheck size={16} />
                                            Currently Active Globally
                                        </div>
                                    )}
                                </div>
                            </motion.button>
                        );
                    })}
                </div>
            </section>

            {/* Micro-interaction Demo */}
            <section className="bg-gradient-to-br from-primary/5 to-accent/5 rounded-[3rem] p-12 text-center border border-primary/10 dark:border-white/5">
                <Heart className="w-12 h-12 mx-auto text-primary mb-4 animate-pulse" />
                <h3 className="text-2xl font-black text-slate-900 dark:text-white">Designed for Trust</h3>
                <p className="text-slate-500 dark:text-slate-400 max-w-lg mx-auto mt-2">
                    Every theme is handcrafted to convey professionalism and build long-term trust with your clients.
                </p>
                <div className="mt-8 flex justify-center gap-4">
                    <div className="px-6 py-2 bg-white dark:bg-slate-800 rounded-full shadow-sm text-sm font-bold flex items-center gap-2">
                        <Zap size={14} className="text-amber-500" /> Premium UX
                    </div>
                </div>
            </section>
        </div>
    );
};

export default ThemeSettings;

import React, { useState, useEffect, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
    Video, VideoOff, Users, Clock, Play, Square, Copy, CheckCircle2,
    MessageSquare, Paperclip, ChevronDown, RefreshCw, Loader2, AlertCircle, Link2
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import WaitingRoomQueue from '@/components/virtualOffice/WaitingRoomQueue';
import ChatPanel from '@/components/virtualOffice/ChatPanel';
import FilePanel from '@/components/virtualOffice/FilePanel';
import MeetingRoom from '@/components/virtualOffice/MeetingRoom';
import api from '@/lib/api';

const SERVER_URL = import.meta.env.VITE_API_BASE_URL || 'http://localhost:5001';

export default function VirtualOfficeManager() {
    const [bookings, setBookings] = useState([]);
    const [selectedBooking, setSelectedBooking] = useState(null);
    const [activeMeeting, setActiveMeeting] = useState(null);
    const [meetingLink, setMeetingLink] = useState('');
    const [copied, setCopied] = useState(false);
    const [loading, setLoading] = useState(false);
    const [appState, setAppState] = useState('idle'); // idle | meeting
    const [rightTab, setRightTab] = useState('queue');
    const [waitingCount, setWaitingCount] = useState(0);
    const [sdkData, setSdkData] = useState(null);

    useEffect(() => { fetchBookings(); }, []);

    async function fetchBookings() {
        try {
            const { data } = await api.get('/virtual-office/scheduled-bookings');
            if (data.success) setBookings(data.bookings);
        } catch { /* silent */ }
    }

    async function handleStartMeeting() {
        if (!selectedBooking) return;
        setLoading(true);
        try {
            // Start meeting
            const { data } = await api.post('/virtual-office/start', { bookingId: selectedBooking.id });
            setActiveMeeting(data.meeting);

            // Generate booking access token for client
            const tokenRes = await api.post('/virtual-office/generate-booking-token', { bookingId: selectedBooking.id });

            // Build client meeting URL
            const clientUrl = `${window.location.origin}/virtual-office?booking=${selectedBooking.id}&token=${tokenRes.data.token}`;
            setMeetingLink(clientUrl);

            // Get admin SDK signature (role=1 = host)
            const sigRes = await api.post('/virtual-office/sdk-signature', {
                zoomMeetingId: data.meeting.zoom_meeting_id,
                role: 1,
            });
            setSdkData({
                ...sigRes.data,
                zoomMeetingId: data.meeting.zoom_meeting_id,
                zoomPassword: data.meeting.zoom_password,
            });

            setAppState('meeting');
        } catch (err) {
            alert('Failed to start meeting: ' + (err.response?.data?.error || err.message));
        } finally {
            setLoading(false);
        }
    }

    async function handleEndMeeting() {
        if (!activeMeeting) return;
        if (!window.confirm('Are you sure you want to end this meeting?')) return;
        setLoading(true);
        try {
            await api.post(`/virtual-office/end/${activeMeeting.id}`);
            setAppState('idle');
            setActiveMeeting(null);
            setSdkData(null);
            setMeetingLink('');
            fetchBookings();
        } catch (err) {
            alert('Failed to end meeting: ' + (err.response?.data?.error || err.message));
        } finally {
            setLoading(false);
        }
    }

    function copyLink() {
        navigator.clipboard.writeText(meetingLink);
        setCopied(true);
        setTimeout(() => setCopied(false), 2500);
    }

    const adminName = 'Sudhir Gupta';

    const statStyles = {
        blue: { bg: 'bg-blue-100 dark:bg-blue-900/20', text: 'text-blue-600 dark:text-blue-400' },
        green: { bg: 'bg-green-100 dark:bg-green-900/20', text: 'text-green-600 dark:text-green-400' },
        slate: { bg: 'bg-slate-100 dark:bg-slate-700', text: 'text-slate-600 dark:text-slate-400' },
    };

    // ΓöÇΓöÇ IDLE: Meeting Setup ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
    if (appState === 'idle') {
        return (
            <div className="space-y-6">
                {/* Header */}
                <div className="flex items-center justify-between">
                    <div>
                        <h1 className="text-2xl font-black text-slate-900 dark:text-white">Virtual Office</h1>
                        <p className="text-sm text-slate-500 mt-1">Select a booking and start a secure video consultation</p>
                    </div>
                    <Button variant="outline" size="sm" onClick={fetchBookings} className="gap-2">
                        <RefreshCw className="w-4 h-4" />Refresh
                    </Button>
                </div>

                {/* Stats row */}
                <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
                    {[
                        { label: 'Pending Bookings', value: bookings.length, icon: Clock, color: 'blue' },
                        { label: 'Active Meeting', value: appState === 'meeting' ? 'Live' : 'None', icon: Video, color: appState === 'meeting' ? 'green' : 'slate' },
                    ].map(stat => {
                        const style = statStyles[stat.color] || statStyles.slate;
                        return (
                            <div key={stat.label} className="bg-white dark:bg-slate-800 rounded-2xl border border-slate-100 dark:border-slate-700 p-5">
                                <div className={`w-10 h-10 rounded-xl ${style.bg} flex items-center justify-center mb-3`}>
                                    <stat.icon className={`w-5 h-5 ${style.text}`} />
                                </div>
                                <p className="text-2xl font-black text-slate-900 dark:text-white">{stat.value}</p>
                                <p className="text-xs text-slate-500 dark:text-slate-400 mt-1">{stat.label}</p>
                            </div>
                        );
                    })}
                </div>

                {/* Booking selector + Start */}
                <div className="bg-white dark:bg-slate-800 rounded-2xl border border-slate-100 dark:border-slate-700 p-6">
                    <h2 className="text-lg font-black text-slate-900 dark:text-white mb-4">Start a Meeting</h2>
                    <div className="space-y-4">
                        <div>
                            <label className="block text-xs font-bold uppercase tracking-widest text-slate-500 dark:text-slate-400 mb-2">Select Booking</label>
                            <select
                                value={selectedBooking?.id || ''}
                                onChange={e => setSelectedBooking(bookings.find(b => b.id === e.target.value) || null)}
                                className="w-full px-4 py-3 rounded-xl border border-slate-200 dark:border-slate-700 bg-slate-50 dark:bg-slate-900 text-slate-900 dark:text-white text-sm focus:outline-none focus:ring-2 focus:ring-blue-500/30"
                            >
                                <option value="">ΓÇö Choose a booking ΓÇö</option>
                                {bookings.map(b => (
                                    <option key={b.id} value={b.id}>
                                        {b.name} ┬╖ {b.service} ┬╖ {new Date(b.date || b.created_at).toLocaleDateString('en-IN')} {b.time || ''}
                                    </option>
                                ))}
                            </select>
                        </div>

                        {selectedBooking && (
                            <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}
                                className="bg-blue-50 dark:bg-blue-900/10 border border-blue-100 dark:border-blue-900/30 rounded-xl p-4 space-y-2 text-sm">
                                {[
                                    ['Client', selectedBooking.name],
                                    ['Email', selectedBooking.email],
                                    ['Service', selectedBooking.service],
                                    ['Date', selectedBooking.date],
                                    ['Time', selectedBooking.time],
                                ].map(([k, v]) => v && (
                                    <div key={k} className="flex gap-3">
                                        <span className="font-bold text-slate-500 dark:text-slate-400 w-16 flex-shrink-0">{k}</span>
                                        <span className="text-slate-800 dark:text-slate-200">{v}</span>
                                    </div>
                                ))}
                            </motion.div>
                        )}

                        <Button
                            onClick={handleStartMeeting}
                            disabled={!selectedBooking || loading}
                            className="w-full h-12 bg-blue-600 hover:bg-blue-700 text-white font-black rounded-xl gap-2 shadow-lg shadow-blue-600/20"
                        >
                            {loading ? <Loader2 className="w-5 h-5 animate-spin" /> : <Play className="w-5 h-5" />}
                            {loading ? 'Starting...' : 'Start Meeting'}
                        </Button>
                    </div>
                </div>

                {/* Recent meetings */}
                <RecentMeetings />
            </div>
        );
    }

    // ΓöÇΓöÇ ACTIVE MEETING ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
    return (
        <div className="h-[calc(100vh-80px)] flex flex-col overflow-hidden -m-4 md:-m-8 p-4 md:p-6">
            {/* Meeting top bar */}
            <div className="flex items-center gap-4 mb-4 flex-shrink-0">
                <div className="flex items-center gap-2">
                    <span className="w-2.5 h-2.5 rounded-full bg-red-500 animate-pulse" />
                    <span className="font-black text-slate-900 dark:text-white text-sm">LIVE ┬╖ {selectedBooking?.name}</span>
                </div>
                <div className="flex-1" />

                {/* Client link */}
                {meetingLink && (
                    <div className="flex items-center gap-2 bg-slate-100 dark:bg-slate-800 rounded-xl px-3 py-2 max-w-xs">
                        <Link2 className="w-3.5 h-3.5 text-slate-400 flex-shrink-0" />
                        <span className="text-xs text-slate-500 truncate flex-1">{meetingLink.substring(0, 40)}ΓÇª</span>
                        <button onClick={copyLink} className="flex-shrink-0">
                            {copied ? <CheckCircle2 className="w-4 h-4 text-emerald-500" /> : <Copy className="w-4 h-4 text-slate-400 hover:text-blue-600" />}
                        </button>
                    </div>
                )}

                <Button variant="destructive" size="sm" onClick={handleEndMeeting} disabled={loading} className="gap-2 rounded-xl">
                    {loading ? <Loader2 className="w-4 h-4 animate-spin" /> : <Square className="w-4 h-4" />}
                    End Meeting
                </Button>
            </div>

            {/* Main: video + right panel */}
            <div className="flex flex-1 gap-4 overflow-hidden min-h-0">
                {/* Video area */}
                <div className="flex-1 min-w-0">
                    {sdkData ? (
                        <MeetingRoom
                            meetingNumber={sdkData.zoomMeetingId}
                            signature={sdkData.signature}
                            sdkKey={sdkData.sdkKey}
                            password={sdkData.zoomPassword}
                            userName={adminName}
                            userEmail="ms.sudhirgupta@rediffmail.com"
                            role={1}
                            onLeave={handleEndMeeting}
                        />
                    ) : (
                        <div className="w-full h-full bg-slate-900 rounded-2xl flex items-center justify-center">
                            <Loader2 className="w-8 h-8 text-blue-400 animate-spin" />
                        </div>
                    )}
                </div>

                {/* Right panel */}
                <div className="w-72 xl:w-80 flex flex-col bg-white dark:bg-slate-900 border border-slate-100 dark:border-slate-800 rounded-2xl overflow-hidden flex-shrink-0">
                    {/* Tabs */}
                    <div className="flex border-b border-slate-100 dark:border-slate-800">
                        {[
                            { id: 'queue', label: 'Waiting', badge: waitingCount },
                            { id: 'chat', label: 'Chat' },
                            { id: 'files', label: 'Files' },
                        ].map(tab => (
                            <button key={tab.id} onClick={() => setRightTab(tab.id)}
                                className={`flex-1 relative py-3 text-xs font-bold transition-colors border-b-2 ${rightTab === tab.id ? 'text-blue-600 border-blue-500' : 'text-slate-400 border-transparent hover:text-slate-600'}`}>
                                {tab.label}
                                {tab.badge > 0 && (
                                    <span className="ml-1 inline-flex items-center justify-center w-4 h-4 text-[10px] font-black rounded-full bg-amber-500 text-white">
                                        {tab.badge}
                                    </span>
                                )}
                            </button>
                        ))}
                    </div>
                    <div className="flex-1 overflow-y-auto p-3 min-h-0">
                        {rightTab === 'queue' && (
                            <WaitingRoomQueue meetingId={activeMeeting?.id} onQueueUpdate={setWaitingCount} />
                        )}
                        {rightTab === 'chat' && (
                            <ChatPanel meetingId={activeMeeting?.id} senderName={adminName} senderRole="admin" />
                        )}
                        {rightTab === 'files' && (
                            <FilePanel meetingId={activeMeeting?.id} uploadedBy="admin" />
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

// ΓöÇ Recent Meetings sub-component ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
function RecentMeetings() {
    const [meetings, setMeetings] = useState([]);

    useEffect(() => {
        api.get('/virtual-office/meetings').then(({ data }) => {
            if (data.success) setMeetings(data.meetings.slice(0, 5));
        }).catch(() => { });
    }, []);

    if (meetings.length === 0) return null;

    return (
        <div className="bg-white dark:bg-slate-800 rounded-2xl border border-slate-100 dark:border-slate-700 p-6">
            <h2 className="text-base font-black text-slate-900 dark:text-white mb-4">Recent Meetings</h2>
            <div className="space-y-3">
                {meetings.map(m => (
                    <div key={m.id} className="flex items-center gap-4 p-3 rounded-xl bg-slate-50 dark:bg-slate-900 border border-slate-100 dark:border-slate-700">
                        <div className={`w-2 h-2 rounded-full flex-shrink-0 ${m.status === 'active' ? 'bg-emerald-500 animate-pulse' : m.status === 'ended' ? 'bg-slate-300 dark:bg-slate-600' : 'bg-amber-400'}`} />
                        <div className="flex-1 min-w-0">
                            <p className="text-sm font-bold text-slate-800 dark:text-slate-200 truncate">{m.bookings?.name || 'Unknown'}</p>
                            <p className="text-xs text-slate-400">{m.bookings?.service} ┬╖ {m.duration_minutes ? `${m.duration_minutes} min` : m.status}</p>
                        </div>
                        {m.recording_url && (
                            <a href={m.recording_url} target="_blank" rel="noreferrer"
                                className="text-xs text-blue-600 dark:text-blue-400 font-bold hover:underline flex-shrink-0">
                                Recording
                            </a>
                        )}
                        <span className={`text-[10px] font-black uppercase px-2 py-0.5 rounded-full flex-shrink-0 ${m.status === 'active' ? 'bg-emerald-100 text-emerald-700 dark:bg-emerald-900/20 dark:text-emerald-400' :
                            m.status === 'ended' ? 'bg-slate-100 text-slate-500 dark:bg-slate-700 dark:text-slate-400' :
                                'bg-amber-100 text-amber-700 dark:bg-amber-900/20 dark:text-amber-400'
                            }`}>{m.status}</span>
                    </div>
                ))}
            </div>
        </div>
    );
}

import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '@/context/useLanguage';
import api from '@/lib/api';
import {
    Award,
    CheckCircle2,
    Users,
    TrendingUp,
    ShieldCheck,
    Target,
    Heart,
    Star,
    ArrowLeft,
    Calendar,
    BarChart2,
    Info
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Link } from 'react-router-dom';

const ICON_MAP = {
    Users, TrendingUp, Award, ShieldCheck, Target, Heart, Star, CheckCircle2, Calendar, BarChart2, Info
};

const AboutDetails = () => {
    const { t } = useLanguage();
    const [sections, setSections] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchContent = async () => {
            try {
                const { data } = await api.get('/about/public');
                setSections(data);
            } catch (err) {
                console.error('Failed to fetch about content:', err);
            } finally {
                setLoading(false);
            }
        };
        fetchContent();
    }, []);

    if (loading) return <div className="min-h-screen flex items-center justify-center">Loading...</div>;

    const milestones = sections.filter(s => s.section_type === 'milestone').sort((a, b) => a.order_index - b.order_index);
    const stats = sections.filter(s => s.section_type === 'stat').sort((a, b) => a.order_index - b.order_index);
    const commitments = sections.filter(s => s.section_type === 'commitment').sort((a, b) => a.order_index - b.order_index);

    const getGeneral = (title, defaultValue) => {
        const item = sections.find(s => s.section_type === 'general' && s.title === title);
        return item ? item.description : defaultValue;
    };

    const heroTitle = getGeneral('Hero Main Title', 'From 2008 to 2024: Building Trust, One Family at a Time');
    const heroHighlight = getGeneral('Hero Highlight Text', 'Building Trust, One Family at a Time');
    const bioParagraphs = [
        getGeneral('Bio Paragraph 1', "My journey in financial services began more than two decades ago with a simple observation: most families worked incredibly hard for their money, but their money didn't work for them. There was a massive gap between earning and strategic wealth creation."),
        getGeneral('Bio Paragraph 2', "In 2008, I decided to bridge this gap. I set out with a mission to simplify finance and bring institutional-grade investment strategies to every household. Whether it's planning for a child's Ivy League education or building a comfortable retirement corpus, I believe every dream deserves a roadmap."),
        getGeneral('Bio Paragraph 3', "Over the years, Raunak Consultancy has evolved into a trusted name for financial advisory. But beyond the numbers and portfolios, what drives me is the \"Peace of Mind\" I see on a parent's face when they know their family's future is secure.")
    ];
    const advisorName = getGeneral('Advisor Name', 'Sudhir Mewalal Gupta');
    const advisorRole = getGeneral('Advisor Role', 'Financial Growth & Claims Expert');
    const primaryFocus = getGeneral('Primary Focus', 'Wealth Creation');
    const philosophy = getGeneral('Philosophy', 'Client Interest First');

    return (
        <div className="min-h-screen bg-white dark:bg-slate-900 pt-24 md:pt-32 pb-16 md:pb-24 transition-colors duration-500">
            <div className="container mx-auto px-6">
                <div className="flex flex-col lg:flex-row gap-8 md:gap-12 lg:gap-16 items-start">
                    {/* Left: Interactive Bio Card */}
                    <div className="lg:w-1/3 sticky top-32">
                        <motion.div
                            initial={{ opacity: 0, x: -50 }}
                            animate={{ opacity: 1, x: 0 }}
                            className="bg-slate-50 dark:bg-slate-800 p-5 md:p-8 rounded-[2rem] md:rounded-[2.5rem] border border-slate-100 dark:border-slate-700 shadow-xl"
                        >
                            <div className="relative mb-8 group">
                                <div className="absolute inset-0 bg-primary-theme rounded-[2rem] rotate-6 group-hover:rotate-3 transition-transform duration-500 -z-10 opacity-20" />
                                <img
                                    src="/images/sudhir-gupta.jpg"
                                    alt={advisorName}
                                    className="w-full aspect-[4/5] object-cover rounded-[2rem] shadow-2xl grayscale-[30%] group-hover:grayscale-0 transition-all duration-500"
                                    onError={(e) => e.target.src = "/advisor.jpg"}
                                />
                                <div className="absolute -bottom-3 -right-3 md:-bottom-6 md:-right-6 bg-primary-theme text-white p-3 md:p-6 rounded-2xl md:rounded-3xl shadow-2xl flex flex-col items-center">
                                    <Star className="w-4 h-4 md:w-6 md:h-6 mb-1 fill-white" />
                                    <span className="text-base md:text-xl font-black">25+</span>
                                    <span className="text-[9px] md:text-[10px] uppercase font-bold tracking-widest text-primary-theme/20">Years</span>
                                </div>
                            </div>

                            <h1 className="text-3xl font-black text-slate-900 dark:text-white mb-2">{advisorName}</h1>
                            <p className="text-primary-theme font-bold mb-6">{advisorRole}</p>

                            <div className="space-y-4">
                                <div className="flex items-center gap-4 p-4 bg-white dark:bg-slate-900 rounded-2xl border border-slate-100 dark:border-slate-700 transition-all hover:scale-105">
                                    <Target className="w-5 h-5 text-rose-500" />
                                    <div>
                                        <p className="text-[10px] uppercase font-bold text-slate-400">Primary Focus</p>
                                        <p className="text-sm font-bold text-slate-700 dark:text-slate-200">{primaryFocus}</p>
                                    </div>
                                </div>
                                <div className="flex items-center gap-4 p-4 bg-white dark:bg-slate-900 rounded-2xl border border-slate-100 dark:border-slate-700 transition-all hover:scale-105">
                                    <Heart className="w-5 h-5 text-primary-theme" />
                                    <div>
                                        <p className="text-[10px] uppercase font-bold text-slate-400">Philosophy</p>
                                        <p className="text-sm font-bold text-slate-700 dark:text-slate-200">{philosophy}</p>
                                    </div>
                                </div>
                            </div>
                        </motion.div>
                    </div>

                    {/* Right: Detailed Journey Content */}
                    <div className="lg:w-2/3">
                        <motion.div
                            initial={{ opacity: 0, y: 50 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.2 }}
                        >
                            <h2 className="text-2xl sm:text-3xl md:text-5xl font-black text-slate-900 dark:text-white mb-6 md:mb-8 leading-tight">
                                {heroTitle.split(':')[0]}: <br />
                                <span className="text-primary-theme">{heroTitle.split(':')[1]?.trim() || heroHighlight}</span>
                            </h2>

                            <div className="prose prose-lg prose-slate dark:prose-invert max-w-none text-slate-600 dark:text-slate-400 font-medium leading-relaxed mb-12">
                                {bioParagraphs.map((para, i) => para && <p key={i}>{para}</p>)}
                            </div>

                            {/* Stats Grid */}
                            <div className="grid grid-cols-2 md:grid-cols-4 gap-3 md:gap-6 mb-12 md:mb-20">
                                {stats.map((stat, i) => {
                                    const Icon = ICON_MAP[stat.icon] || BarChart2;
                                    return (
                                        <div key={i} className="text-center p-4 md:p-6 bg-slate-50 dark:bg-slate-800/50 rounded-2xl md:rounded-3xl border border-slate-100 dark:border-slate-800 transition-all hover:bg-white dark:hover:bg-slate-800">
                                            <Icon className={`w-8 h-8 text-primary-theme mx-auto mb-4`} />
                                            <h4 className="text-2xl font-black text-slate-900 dark:text-white mb-1">{stat.value}</h4>
                                            <p className="text-[10px] uppercase font-bold text-slate-500 tracking-wider leading-tight">{stat.title}</p>
                                        </div>
                                    );
                                })}
                            </div>

                            {/* Timeline / Milestones */}
                            <h3 className="text-3xl font-black text-slate-900 dark:text-white mb-10 flex items-center gap-3">
                                <TrendingUp className="text-primary-theme" />
                                Growth Milestones
                            </h3>
                            <div className="relative border-l-2 border-slate-100 dark:border-slate-800 ml-4 space-y-12 pb-12">
                                {milestones.map((item, i) => (
                                    <div key={i} className="relative pl-12 group">
                                        <div className="absolute -left-[9px] top-1 w-4 h-4 rounded-full bg-primary-theme border-4 border-white dark:border-slate-900 group-hover:scale-150 transition-transform duration-300" />
                                        <span className="text-primary-theme font-black text-sm uppercase tracking-widest">{item.year}</span>
                                        <h4 className="text-xl font-bold text-slate-900 dark:text-white mt-1 mb-2">{item.title}</h4>
                                        <p className="text-slate-600 dark:text-slate-400 font-medium whitespace-pre-wrap">{item.description}</p>
                                    </div>
                                ))}
                            </div>

                            {/* My Vision Table */}
                            <div className="bg-primary-theme rounded-[2rem] md:rounded-[3rem] p-6 md:p-12 text-white overflow-hidden relative">
                                <div className="absolute top-0 right-0 w-64 h-64 bg-white/10 blur-[80px] rounded-full -mr-32 -mt-32" />
                                <div className="relative z-10">
                                    <h3 className="text-xl md:text-3xl font-black mb-5 md:mb-8 leading-tight">My Commitment to You</h3>
                                    <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                                        {commitments.map((promise, i) => (
                                            <div key={i} className="flex gap-4">
                                                <CheckCircle2 className="w-10 h-10 text-white/40 flex-shrink-0" />
                                                <div>
                                                    <h5 className="font-black text-lg mb-2">{promise.title}</h5>
                                                    <p className="text-white/80 text-sm leading-relaxed">{promise.description}</p>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>

                            <div className="mt-16 text-center">
                                <Link to="/certificates">
                                    <Button size="lg" className="h-16 px-12 rounded-full bg-primary-theme hover:opacity-90 text-lg font-black shadow-2xl shadow-primary-theme/30 transition-all hover:scale-105">
                                        View Professional Certifications
                                    </Button>
                                </Link>
                            </div>
                        </motion.div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AboutDetails;

import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useLanguage } from '@/context/useLanguage';
import { Calendar, User, ArrowLeft, Clock, Share2, Tag } from 'lucide-react';
import { Button } from '@/components/ui/button';
import api from '@/lib/api';

const BlogDetail = () => {
    const { slug } = useParams();
    const { t } = useLanguage();
    const [blog, setBlog] = useState(null);
    const [loading, setLoading] = useState(true);

    const estimateReadTime = (htmlContent) => {
        if (!htmlContent) return 1;
        const text = htmlContent.replace(/<[^>]*>/g, '');
        const words = text.trim().split(/\s+/).length;
        return Math.max(1, Math.ceil(words / 200));
    };

    useEffect(() => {
        const fetchBlog = async () => {
            try {
                const { data } = await api.get(`/blogs/${slug}`);
                setBlog(data);
            } catch (error) {
                console.error('Error fetching blog:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchBlog();
        window.scrollTo(0, 0);
    }, [slug]);

    if (loading) {
        return (
            <div className="min-h-screen bg-white dark:bg-slate-900 pt-32 flex items-center justify-center">
                <div className="w-16 h-16 border-4 border-primary-theme border-t-transparent rounded-full animate-spin" />
            </div>
        );
    }

    if (!blog) {
        return (
            <div className="min-h-screen bg-white dark:bg-slate-900 pt-32 text-center">
                <h2 className="text-3xl font-black text-slate-900 dark:text-white">Blog not found</h2>
                <Link to="/blogs" className="text-primary-theme mt-4 inline-block font-bold underline">Back to Blogs</Link>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-white dark:bg-slate-900 pt-32 pb-24 transition-colors duration-500">
            <div className="container mx-auto px-6 max-w-4xl">
                {/* Header Actions */}
                <div className="flex justify-between items-center mb-12">
                    <Link to="/blogs" className="inline-flex items-center text-slate-900 dark:text-white hover:text-primary-theme font-bold group transition-colors">
                        <ArrowLeft className="mr-2 w-5 h-5 group-hover:-translate-x-1 transition-transform" />
                        Back to Insights
                    </Link>
                    <Button variant="ghost" size="icon" className="rounded-full">
                        <Share2 className="w-5 h-5" />
                    </Button>
                </div>

                {/* Article Header */}
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="mb-12"
                >
                    <div className="flex flex-wrap items-center gap-6 text-sm text-slate-400 font-bold uppercase tracking-widest mb-6">
                        <div className="flex items-center gap-2 px-3 py-1 bg-blue-50 dark:bg-blue-900/30 text-blue-600 dark:text-blue-400 rounded-lg">
                            <Tag className="w-4 h-4" />
                            {blog.category}
                        </div>
                        <div className="flex items-center gap-1.5">
                            <Calendar className="w-4 h-4" />
                            {new Date(blog.created_at).toLocaleDateString(undefined, { day: 'numeric', month: 'long', year: 'numeric' })}
                        </div>
                        <div className="flex items-center gap-1.5">
                            <Clock className="w-4 h-4" />
                            {estimateReadTime(blog.content)} min read
                        </div>
                    </div>

                    <h1 className="text-4xl md:text-6xl font-black text-slate-900 dark:text-white mb-8 leading-tight">
                        {blog.title}
                    </h1>

                    <div className="flex items-center gap-4 p-6 bg-slate-50 dark:bg-slate-800/50 rounded-3xl border border-slate-100 dark:border-slate-800">
                        <div className="w-12 h-12 bg-primary-theme rounded-full flex items-center justify-center text-white font-black">
                            SG
                        </div>
                        <div>
                            <p className="text-[10px] uppercase font-bold text-slate-400">Written by</p>
                            <p className="text-lg font-black text-slate-900 dark:text-white leading-tight">{blog.author}</p>
                        </div>
                    </div>
                </motion.div>

                {/* Featured Image */}
                <motion.div
                    initial={{ opacity: 0, scale: 0.95 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ delay: 0.2 }}
                    className="mb-12"
                >
                    <img
                        src={blog.image_url || 'https://images.unsplash.com/photo-1454165833767-1330084bc6f8?auto=format&fit=crop&q=80&w=1200'}
                        alt={blog.title}
                        className="w-full aspect-video object-cover rounded-[3rem] shadow-2xl"
                    />
                </motion.div>

                {/* Article Content */}
                <motion.article
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.4 }}
                    className="prose prose-lg md:prose-xl prose-slate dark:prose-invert max-w-none 
                        prose-headings:font-black prose-headings:text-slate-900 dark:prose-headings:text-white
                        prose-p:text-slate-600 dark:prose-p:text-slate-300 prose-p:leading-relaxed
                        prose-strong:font-black prose-strong:text-primary-theme
                        prose-img:rounded-[2rem] prose-blockquote:border-primary-theme prose-blockquote:bg-primary-theme/5 
                        dark:prose-blockquote:bg-primary-theme/10 prose-blockquote:p-6 prose-blockquote:rounded-2xl"
                >
                    {/* Render HTML content safely */}
                    <div dangerouslySetInnerHTML={{ __html: blog.content }} />
                </motion.article>

                <hr className="my-16 border-slate-100 dark:border-slate-800" />

                {/* Related CTA */}
                <div className="bg-slate-900 dark:bg-primary-theme rounded-[3rem] p-12 text-white text-center relative overflow-hidden">
                    <div className="absolute top-0 left-0 w-64 h-64 bg-white/10 blur-[80px] rounded-full -ml-32 -mt-32" />
                    <h3 className="text-3xl font-black mb-6 relative z-10">Have questions about this topic?</h3>
                    <p className="text-slate-400 dark:text-white/80 mb-10 text-lg max-w-2xl mx-auto relative z-10">
                        Schedule a free one-on-one consultation with Sudhir Gupta to discuss your specific financial goals.
                    </p>
                    <Link to="/" className="relative z-10">
                        <Button className="bg-white text-slate-900 hover:bg-slate-50 rounded-full h-16 px-10 text-lg font-black transition-all hover:scale-105">
                            Book Free Consultation
                        </Button>
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default BlogDetail;

