export default Home;

import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useAuth } from '@/context/AuthContext';
import { useLanguage } from '@/context/useLanguage';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { LogIn, Mail, Lock, ArrowRight, ShieldCheck } from 'lucide-react';

const Login = () => {
    const { t } = useLanguage();
    const { login } = useAuth();
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        const result = await login(email, password);
        if (result.success) {
            navigate('/');
        } else {
            setError(result.error || 'Login failed');
        }
        setLoading(false);
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-slate-50 dark:bg-slate-900 pt-20 px-6 transition-colors duration-500">
            <div className="absolute inset-0 z-0 overflow-hidden pointer-events-none">
                <div className="absolute top-[20%] -left-[10%] w-[35%] h-[35%] bg-primary-theme/10 blur-[120px] rounded-full"></div>
                <div className="absolute bottom-[20%] -right-[10%] w-[35%] h-[35%] bg-accent-theme/10 blur-[120px] rounded-full"></div>
            </div>

            <motion.div
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
                className="w-full max-w-md relative z-10"
            >
                <div className="bg-white dark:bg-slate-800 p-8 md:p-10 rounded-[2.5rem] shadow-2xl shadow-blue-500/5 border border-slate-100 dark:border-slate-700">
                    <div className="text-center mb-10">
                        <div className="w-16 h-16 bg-primary-theme rounded-2xl flex items-center justify-center mx-auto mb-6 shadow-lg shadow-primary-theme/20">
                            <LogIn className="text-white w-8 h-8" />
                        </div>
                        <h1 className="text-3xl font-black text-slate-900 dark:text-white mb-2 tracking-tight">{t.auth.loginTitle}</h1>
                        <p className="text-slate-500 dark:text-slate-400 text-sm leading-relaxed">{t.auth.loginSubtitle}</p>
                    </div>

                    <div className="bg-amber-50 dark:bg-amber-900/20 text-amber-600 dark:text-amber-400 p-4 rounded-2xl text-[10px] font-bold border border-amber-100 dark:border-amber-900/50 flex flex-col items-center gap-2 mb-6">
                        <ShieldCheck className="w-5 h-5 mb-1" />
                        <p className="text-center">USER LOGIN IS CURRENTLY DISABLED</p>
                        <p className="text-center font-normal opacity-80 uppercase tracking-tighter">Only Admin access is permitted at this time.</p>
                    </div>

                    <form onSubmit={handleSubmit} className="space-y-6 opacity-50 pointer-events-none">
                        {error && (
                            <motion.div
                                initial={{ opacity: 0, height: 0 }}
                                animate={{ opacity: 1, height: 'auto' }}
                                className="bg-red-50 dark:bg-red-900/20 text-red-600 dark:text-red-400 p-4 rounded-2xl text-xs font-bold border border-red-100 dark:border-red-900/50 flex items-center gap-3"
                            >
                                <div className="w-1.5 h-1.5 rounded-full bg-red-600" />
                                {error}
                            </motion.div>
                        )}

                        <div className="space-y-2">
                            <Label htmlFor="email">{t.auth.email}</Label>
                            <div className="relative group">
                                <Mail className="absolute left-4 top-3.5 w-5 h-5 text-slate-400 group-focus-within:text-blue-600 transition-colors" aria-hidden="true" />
                                <Input
                                    id="email"
                                    type="email"
                                    placeholder="name@example.com"
                                    className="pl-12 h-12 rounded-2xl border-slate-200 dark:border-slate-700 focus:ring-blue-600 transition-all"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    autoComplete="email"
                                    required
                                    disabled
                                />
                            </div>
                        </div>

                        <div className="space-y-2">
                            <div className="flex justify-between items-center">
                                <Label htmlFor="password">{t.auth.password}</Label>
                                <a href="#" className="text-xs font-bold text-blue-600 dark:text-blue-400 hover:underline">{t.auth.forgotPassword}</a>
                            </div>
                            <div className="relative group">
                                <Lock className="absolute left-4 top-3.5 w-5 h-5 text-slate-400 group-focus-within:text-blue-600 transition-colors" aria-hidden="true" />
                                <Input
                                    id="password"
                                    type="password"
                                    placeholder="ΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇó"
                                    className="pl-12 h-12 rounded-2xl border-slate-200 dark:border-slate-700 focus:ring-blue-600 transition-all"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    autoComplete="current-password"
                                    required
                                    disabled
                                />
                            </div>
                        </div>

                        <Button
                            type="submit"
                            disabled={true}
                            className="w-full h-12 rounded-2xl bg-blue-600 hover:bg-blue-700 shadow-xl shadow-blue-600/20 font-bold transition-all"
                        >
                            {t.auth.loginBtn}
                        </Button>
                    </form>

                    {/* 
                    <div className="mt-10 pt-8 border-t border-slate-100 dark:border-slate-700/50 text-center">
                        <p className="text-sm text-slate-500 dark:text-slate-400 mb-4 ">
                            {t.auth.noAccount}
                        </p>
                        <Link to="/register">
                            <Button variant="outline" className="w-full h-12 rounded-2xl border-2 font-bold group">
                                {t.auth.registerBtn} <ArrowRight className="ml-2 w-4 h-4 group-hover:translate-x-1 transition-transform" />
                            </Button>
                        </Link>
                    </div>
                    */}

                    <div className="mt-6 flex items-center justify-center gap-2 text-[10px] font-bold text-slate-400 uppercase tracking-widest">
                        <ShieldCheck className="w-3 h-3" />
                        Secure Encrypted Session
                    </div>
                </div>
            </motion.div>
        </div>
    );
};

export default Login;

import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useAuth } from '@/context/AuthContext';
import { useLanguage } from '@/context/useLanguage';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import api from '@/lib/api';
import { UserPlus, Mail, Lock, User, Phone, ArrowLeft, CheckCircle } from 'lucide-react';

const Register = () => {
    const { t } = useLanguage();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        fullName: '',
        email: '',
        password: '',
        mobile: ''
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [success, setSuccess] = useState(false);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.id]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const { data } = await api.post('/auth/register', formData);
            if (data.success) {
                setSuccess(true);
                setTimeout(() => navigate('/login'), 2000);
            }
        } catch (err) {
            setError(err.response?.data?.error || 'Registration failed');
        } finally {
            setLoading(false);
        }
    };

    if (success) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-slate-50 dark:bg-slate-900 px-6">
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="max-w-md w-full text-center p-8 bg-white dark:bg-slate-800 rounded-[2.5rem] shadow-2xl border border-slate-100 dark:border-slate-700"
                >
                    <div className="w-20 h-20 bg-green-100 dark:bg-green-900/30 rounded-full flex items-center justify-center mx-auto mb-6">
                        <CheckCircle className="w-10 h-10 text-green-600" />
                    </div>
                    <h2 className="text-2xl font-black text-slate-900 dark:text-white mb-2">Registration Successful!</h2>
                    <p className="text-slate-500 dark:text-slate-400">Welcome to the family. Redirecting to login...</p>
                </motion.div>
            </div>
        );
    }

    return (
        <div className="min-h-screen flex items-center justify-center bg-slate-50 dark:bg-slate-900 pt-24 pb-12 px-6 transition-colors duration-500">
            <div className="absolute inset-0 z-0 overflow-hidden pointer-events-none">
                <div className="absolute -top-[10%] -right-[10%] w-[40%] h-[40%] bg-blue-400/10 dark:bg-blue-600/5 blur-[120px] rounded-full"></div>
            </div>

            <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                className="w-full max-w-lg relative z-10"
            >
                <div className="bg-white dark:bg-slate-800 p-8 md:p-10 rounded-[2.5rem] shadow-2xl border border-slate-100 dark:border-slate-700">
                    <div className="mb-10 flex flex-col items-center text-center">
                        <div className="w-16 h-16 bg-blue-50 dark:bg-blue-900/40 rounded-2xl flex items-center justify-center mb-6">
                            <UserPlus className="text-primary-theme w-8 h-8" />
                        </div>
                        <h1 className="text-3xl font-black text-slate-900 dark:text-white mb-2 tracking-tight">{t.auth.registerTitle}</h1>
                        <p className="text-slate-500 dark:text-slate-400 text-sm">{t.auth.registerSubtitle}</p>
                    </div>

                    <div className="bg-amber-50 dark:bg-amber-900/20 text-amber-600 dark:text-amber-400 p-4 rounded-2xl text-[10px] font-bold border border-amber-100 dark:border-amber-900/50 flex flex-col items-center gap-2 mb-8">
                        <CheckCircle className="w-5 h-5 mb-1" />
                        <p className="text-center">REGISTRATION IS TEMPORARILY DISABLED</p>
                        <p className="text-center font-normal opacity-80 uppercase tracking-tighter">New user accounts are not being accepted at this time.</p>
                    </div>

                    <form onSubmit={handleSubmit} className="space-y-5 opacity-50 pointer-events-none">
                        {error && (
                            <div className="bg-red-50 dark:bg-red-900/20 text-red-600 dark:text-red-400 p-4 rounded-2xl text-xs font-bold border border-red-100 dark:border-red-900/50">
                                {error}
                            </div>
                        )}

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-5">
                            <div className="space-y-2">
                                <Label htmlFor="fullName">{t.auth.fullName}</Label>
                                <div className="relative group">
                                    <User className="absolute left-4 top-3.5 w-4 h-4 text-slate-400 group-focus-within:text-primary-theme transition-colors" />
                                    <Input
                                        id="fullName"
                                        placeholder="John Doe"
                                        className="pl-10 h-11 rounded-2xl border-slate-200 dark:border-slate-700 transition-all text-sm"
                                        value={formData.fullName}
                                        onChange={handleChange}
                                        required
                                        disabled
                                    />
                                </div>
                            </div>
                            <div className="space-y-2">
                                <Label htmlFor="mobile">{t.auth.mobile}</Label>
                                <div className="relative group">
                                    <Phone className="absolute left-4 top-3.5 w-4 h-4 text-slate-400 group-focus-within:text-primary-theme transition-colors" />
                                    <Input
                                        id="mobile"
                                        placeholder="98765 43210"
                                        className="pl-10 h-11 rounded-2xl border-slate-200 dark:border-slate-700 transition-all text-sm"
                                        value={formData.mobile}
                                        onChange={handleChange}
                                        required
                                        disabled
                                    />
                                </div>
                            </div>
                        </div>

                        <div className="space-y-2">
                            <Label htmlFor="email">{t.auth.email}</Label>
                            <div className="relative group">
                                <Mail className="absolute left-4 top-3.5 w-4 h-4 text-slate-400 group-focus-within:text-primary-theme transition-colors" />
                                <Input
                                    id="email"
                                    type="email"
                                    placeholder="name@example.com"
                                    className="pl-10 h-11 rounded-2xl border-slate-200 dark:border-slate-700 transition-all text-sm"
                                    value={formData.email}
                                    onChange={handleChange}
                                    required
                                    disabled
                                />
                            </div>
                        </div>

                        <div className="space-y-2">
                            <Label htmlFor="password">{t.auth.password}</Label>
                            <div className="relative group">
                                <Lock className="absolute left-4 top-3.5 w-4 h-4 text-slate-400 group-focus-within:text-blue-600 transition-colors" />
                                <Input
                                    id="password"
                                    type="password"
                                    placeholder="ΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇó"
                                    className="pl-10 h-11 rounded-2xl border-slate-200 dark:border-slate-700 transition-all text-sm"
                                    value={formData.password}
                                    onChange={handleChange}
                                    autoComplete="new-password"
                                    required
                                    disabled
                                />
                            </div>
                        </div>

                        <Button
                            type="submit"
                            disabled={true}
                            className="w-full h-12 rounded-2xl bg-blue-600 hover:bg-blue-700 font-bold mt-4 shadow-lg shadow-blue-600/10 transition-all"
                        >
                            {t.auth.registerBtn}
                        </Button>
                    </form>

                    <div className="mt-8 pt-8 border-t border-slate-100 dark:border-slate-700/50 text-center">
                        <span className="text-sm text-slate-500 dark:text-slate-400 mr-2">{t.auth.hasAccount}</span>
                        <Link to="/login" className="text-sm font-bold text-blue-600 dark:text-blue-400 hover:underline inline-flex items-center gap-1">
                            {t.auth.loginBtn}
                        </Link>

                    </div>
                </div>
            </motion.div>
        </div>
    );
};

export default Register;

import React, { useState, useEffect, useCallback } from 'react';
import { useSearchParams } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { Clock, CheckCircle2, XCircle, Video, MessageSquare, Paperclip, Users, Loader2, Shield } from 'lucide-react';
import MeetingRoom from '@/components/virtualOffice/MeetingRoom';
import ChatPanel from '@/components/virtualOffice/ChatPanel';
import FilePanel from '@/components/virtualOffice/FilePanel';
import api from '@/lib/api';

// ΓöÇ States: validating ΓåÆ waiting ΓåÆ admitted ΓåÆ rejected ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
export default function VirtualOffice() {
    const [searchParams] = useSearchParams();
    const bookingId = searchParams.get('booking');
    const token = searchParams.get('token');

    const [state, setState] = useState('validating'); // validating | waiting | admitted | rejected | error
    const [error, setError] = useState('');
    const [meetingData, setMeetingData] = useState(null);    // { meetingId, zoomMeetingId, zoomPassword, signature, sdkKey }
    const [waitingEntry, setWaitingEntry] = useState(null);
    const [rightTab, setRightTab] = useState('chat');

    // User info ΓÇö collected from URL or defaults
    const [userName, setUserName] = useState('');
    const [userEmail, setUserEmail] = useState('');
    const [nameSubmitted, setNameSubmitted] = useState(false);

    // ΓöÇΓöÇ Step 1: Validate booking ΓåÆ get meeting ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
    const validateAndGetMeeting = useCallback(async () => {
        if (!bookingId || !token) return;
        try {
            const { data } = await api.get(`/virtual-office/join/${bookingId}`, {
                headers: { 'x-booking-token': token },
                params: { token },
            });

            if (data.success) {
                setMeetingData(data);
                // Enter waiting room
                const wrRes = await api.post(
                    `/virtual-office/enter-waiting/${data.meetingId}`,
                    { userName, userEmail, bookingId },
                    { headers: { 'x-booking-token': token }, params: { token } }
                );
                setWaitingEntry(wrRes.data.waitingEntry);
                setState('waiting');
            }
        } catch (err) {
            const msg = err.response?.data?.error || err.message;
            if (msg.includes('No active meeting')) {
                setError('The advisor hasn\'t started the meeting yet. Please wait and try again shortly.');
            } else {
                setError(msg || 'Failed to join. Please check your booking link.');
            }
            setState('error');
        }
    }, [bookingId, token, userName, userEmail]);

    useEffect(() => {
        if (nameSubmitted) validateAndGetMeeting();
    }, [nameSubmitted]);

    // ΓöÇΓöÇ Step 2: Poll for admission status ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
    useEffect(() => {
        if (state !== 'waiting' || !meetingData?.meetingId) return;
        const interval = setInterval(async () => {
            try {
                const { data } = await api.get(
                    `/virtual-office/admission-status/${meetingData.meetingId}`,
                    {
                        headers: { 'x-booking-token': token },
                        params: { token, email: userEmail },
                    }
                );
                if (data.status?.status === 'admitted') setState('admitted');
                if (data.status?.status === 'rejected') setState('rejected');
            } catch { /* silent */ }
        }, 3000);
        return () => clearInterval(interval);
    }, [state, meetingData, token, userEmail]);

    // ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
    // SCREEN: Name / Email form (before joining)
    if (!nameSubmitted) {
        return (
            <div className="min-h-screen bg-gradient-to-br from-slate-900 via-primary-theme/20 to-slate-900 flex items-center justify-center p-6 pt-32">
                <motion.div initial={{ opacity: 0, y: 24 }} animate={{ opacity: 1, y: 0 }}
                    className="w-full max-w-md bg-white/5 backdrop-blur-2xl border border-white/10 rounded-3xl p-8 shadow-2xl">
                    <div className="flex justify-center mb-6">
                        <div className="w-16 h-16 rounded-2xl bg-primary-theme flex items-center justify-center shadow-lg shadow-primary-theme/30">
                            <Video className="w-8 h-8 text-white" />
                        </div>
                    </div>
                    <h1 className="text-2xl font-black text-white text-center mb-1">Virtual Office</h1>
                    <p className="text-sm text-slate-400 text-center mb-8">Raunak Consultancy ┬╖ Secure Meeting</p>
                    <div className="space-y-4">
                        <div>
                            <label className="text-xs font-bold text-slate-400 uppercase tracking-widest block mb-2">Your Name</label>
                            <input type="text" value={userName} onChange={e => setUserName(e.target.value)}
                                placeholder="e.g. Rahul Sharma"
                                className="w-full px-4 py-3 rounded-xl bg-white/10 border border-white/20 text-white placeholder:text-slate-500 focus:outline-none focus:ring-2 focus:ring-primary-theme/50 text-sm" />
                        </div>
                        <div>
                            <label className="text-xs font-bold text-slate-400 uppercase tracking-widest block mb-2">Your Email</label>
                            <input type="email" value={userEmail} onChange={e => setUserEmail(e.target.value)}
                                placeholder="your@email.com"
                                className="w-full px-4 py-3 rounded-xl bg-white/10 border border-white/20 text-white placeholder:text-slate-500 focus:outline-none focus:ring-2 focus:ring-blue-500/50 text-sm" />
                        </div>
                        <button
                            onClick={() => { if (userName.trim() && userEmail.trim()) setNameSubmitted(true); }}
                            disabled={!userName.trim() || !userEmail.trim()}
                            className="w-full py-3 rounded-xl bg-primary-theme hover:opacity-90 text-white font-bold transition-colors disabled:opacity-40 mt-2">
                            Join Meeting ΓåÆ
                        </button>
                    </div>
                    <div className="flex items-center gap-2 mt-6 text-xs text-slate-500 justify-center">
                        <Shield className="w-3.5 h-3.5" />
                        End-to-end encrypted ┬╖ Zoom powered
                    </div>
                </motion.div>
            </div>
        );
    }

    // SCREEN: Validating
    if (state === 'validating') {
        return <FullScreenStatus icon={<Loader2 className="w-8 h-8 text-primary-theme animate-spin" />} title="Validating your access..." subtitle="Please wait while we verify your booking." />;
    }

    // SCREEN: Error
    if (state === 'error') {
        return <FullScreenStatus icon={<XCircle className="w-8 h-8 text-red-400" />} title="Cannot Join" subtitle={error} color="red" />;
    }

    // SCREEN: Rejected
    if (state === 'rejected') {
        return <FullScreenStatus icon={<XCircle className="w-8 h-8 text-red-400" />} title="Entry Declined" subtitle="The advisor has declined your meeting request. Please contact support." color="red" />;
    }

    // SCREEN: Waiting Room
    if (state === 'waiting') {
        return (
            <div className="min-h-screen bg-gradient-to-br from-slate-900 via-primary-theme/20 to-slate-900 flex items-center justify-center p-6 pt-32">
                <motion.div initial={{ opacity: 0, scale: 0.95 }} animate={{ opacity: 1, scale: 1 }}
                    className="w-full max-w-md text-center bg-white/5 backdrop-blur-2xl border border-white/10 rounded-3xl p-10 shadow-2xl">
                    <div className="relative inline-flex mb-6">
                        <div className="w-20 h-20 rounded-2xl bg-amber-500/20 border border-amber-500/30 flex items-center justify-center">
                            <Clock className="w-10 h-10 text-amber-400" />
                        </div>
                        <span className="absolute -top-1 -right-1 w-4 h-4 rounded-full bg-amber-400 animate-ping" />
                    </div>
                    <h2 className="text-2xl font-black text-white mb-2">You're in the Waiting Room</h2>
                    <p className="text-slate-400 text-sm mb-6">The advisor will admit you shortly. This page refreshes automatically.</p>
                    <div className="bg-white/5 border border-white/10 rounded-2xl p-4 text-left space-y-2">
                        <div className="flex justify-between text-sm"><span className="text-slate-400">Name</span><span className="text-white font-bold">{userName}</span></div>
                        <div className="flex justify-between text-sm"><span className="text-slate-400">Status</span><span className="text-amber-400 font-bold">Waiting</span></div>
                    </div>
                    <div className="flex items-center justify-center gap-2 mt-6 text-xs text-slate-500">
                        <Loader2 className="w-3.5 h-3.5 animate-spin" />Checking for admission every 3 secondsΓÇª
                    </div>
                </motion.div>
            </div>
        );
    }

    // SCREEN: Admitted ΓÇö Full Meeting Room
    return (
        <div className="h-screen bg-slate-900 flex flex-col overflow-hidden">
            {/* Top bar */}
            <div className="h-12 flex items-center justify-between px-4 bg-slate-950/80 border-b border-white/10 flex-shrink-0">
                <div className="flex items-center gap-2">
                    <div className="w-2 h-2 rounded-full bg-emerald-400 animate-pulse" />
                    <span className="text-sm font-bold text-white">Virtual Office ┬╖ Live</span>
                </div>
                <div className="flex items-center gap-2 text-xs text-slate-400">
                    <Video className="w-3.5 h-3.5" />
                    <span>{userName}</span>
                </div>
            </div>

            {/* Main layout: video left + right panel */}
            <div className="flex-1 flex overflow-hidden">
                {/* Video */}
                <div className="flex-1 p-4 min-w-0">
                    <MeetingRoom
                        meetingNumber={meetingData?.zoomMeetingId}
                        signature={meetingData?.signature}
                        sdkKey={meetingData?.sdkKey}
                        password={meetingData?.zoomPassword}
                        userName={userName}
                        userEmail={userEmail}
                        role={0}
                        onLeave={() => window.location.href = '/'}
                    />
                </div>

                {/* Right Panel */}
                <div className="w-80 flex flex-col border-l border-white/10 bg-slate-950/50 flex-shrink-0">
                    {/* Tabs */}
                    <div className="flex border-b border-white/10">
                        {[
                            { id: 'chat', icon: MessageSquare, label: 'Chat' },
                            { id: 'files', icon: Paperclip, label: 'Files' },
                        ].map(tab => (
                            <button key={tab.id} onClick={() => setRightTab(tab.id)}
                                className={`flex-1 flex items-center justify-center gap-1.5 py-3 text-xs font-bold transition-colors ${rightTab === tab.id ? 'text-primary-theme border-b-2 border-primary-theme' : 'text-slate-400 hover:text-slate-200'}`}>
                                <tab.icon className="w-3.5 h-3.5" />{tab.label}
                            </button>
                        ))}
                    </div>

                    {/* Tab content */}
                    <div className="flex-1 overflow-hidden p-3">
                        {rightTab === 'chat' && (
                            <ChatPanel meetingId={meetingData?.meetingId} senderName={userName} senderRole="client" bookingToken={token} />
                        )}
                        {rightTab === 'files' && (
                            <FilePanel meetingId={meetingData?.meetingId} uploadedBy={userEmail} bookingToken={token} />
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

// ΓöÇ Helper: Full-screen status card ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
function FullScreenStatus({ icon, title, subtitle, color = 'blue' }) {
    const colorMap = { blue: 'bg-primary-theme/20 border-primary-theme/30', red: 'bg-red-600/20 border-red-500/30' };
    return (
        <div className="min-h-screen bg-gradient-to-br from-slate-900 via-primary-theme/20 to-slate-900 flex items-center justify-center p-6 pt-32">
            <motion.div initial={{ opacity: 0, y: 24 }} animate={{ opacity: 1, y: 0 }}
                className="text-center bg-white/5 backdrop-blur-2xl border border-white/10 rounded-3xl p-10 shadow-2xl max-w-sm w-full">
                <div className={`mx-auto w-20 h-20 rounded-2xl border ${colorMap[color]} flex items-center justify-center mb-6`}>{icon}</div>
                <h2 className="text-xl font-black text-white mb-2">{title}</h2>
                <p className="text-slate-400 text-sm">{subtitle}</p>
            </motion.div>
        </div>
    );
}
